import React from "react";

const Card = (prop) => {
    const {id, name, email} = prop;
    return (
        <div className="bg-light-green dib br3 pa3 ma2 grow shadow-5">
            <img alt='robots' src={`https://robohash.org/${id}?200*200`} />
            <div>
                <h2>{name}</h2>
                <p>{email}</p>
            </div>
        </div>
    );
}

export default Card;
