import React from "react";
import CardList from './CardList';
import SearchBox from './SearchBox';
import './App.css';

class App extends React.Component {
    constructor(props) { //Any app that owns the state implements constructor to this.state
        super();
        this.state = { //App with two states robots and searchField, which describes the App
            robots : [],
            searchField : ''
        }
    }

    componentDidMount() {
        fetch('https://jsonplaceholder.typicode.com/users')
        .then(response => response.json())
        .then(user => this.setState({robots : user}))
    }
    // onSearchChange lets the app know on change of search text and updates 
    // the value of searchField with the added text
    onSearchChange = (event) => {
        this.setState({ searchField: event.target.value });
    }


    render() {
        //enter text from searchField calls robots.js to return 
        //values to the filteredRobots variable with whatever is searched
        const filteredRobots = this.state.robots.filter(robots => {
            return robots.name.toLowerCase().includes(this.state.searchField.toLowerCase())
        })

        //We passed the filteredRobots array list to CardList component
        return(
            <div className="tc">
            <h1 className="f1">RoboFriends</h1>
            <SearchBox searchChange={this.onSearchChange}/>
            <CardList robots={filteredRobots}/>
            </div>
            );
        }
    }
    export default App;
